css:
.outer-border {
    font-family: "Inter" sans-serif;
    font-size: 16px;
    border-radius: 0.5rem;
    border: 1px solid #E2E6E9;
    padding: 1rem;
}

.inner-border {
    border: 8px solid #0089FF;
    border-radius: 8px;
    text-align: center;
    padding: 6rem 4rem;
    background-color: #FFFFFF;
}

.certificate-logo {
    height: 1.5rem;
    margin-bottom: 4rem;
}

.certificate-name {
    font-size: 2rem;
    font-weight: 500;
    color: #192734;
    margin-bottom: 0.5rem;
}

.certificate-footer {
    margin: 4rem auto 0;
    width: 70%;
    text-align: center;
}

.certificate-footer-item {
    color: #192734;
}

.cursive-font {
    font-family: cursive;
    font-weight: 600;
}

.certificate-divider {
    margin: 0.5rem 0;
}

.certificate-expiry {
    margin-left: 2rem;
}

html:
{% set certificate = frappe.db.get_value("LMS Certificate", doc.name, ["name", "member", "issue_date", "expiry_date", "course"], as_dict=True) %}
{% set member = frappe.db.get_value("User", doc.member, ["full_name"], as_dict=True) %}
{% set course = frappe.db.get_value("LMS Course", doc.course, ["title", "name", "image"], as_dict=True) %}
{% set logo = frappe.db.get_single_value("Website Settings", "banner_image") %}
{% set instructors = frappe.get_all("Course Instructor", {"parent": doc.course}, pluck="instructor", order_by="idx") %}

<meta name="pdfkit-orientation" content="Landscape">
<link rel="preconnect" href="https://fonts.googleapis.com">
<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
<link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500&display=swap" rel="stylesheet">

<div>
    <div class="inner-border">

        {% if logo %}
        <img src="{{ logo }}" class="certificate-logo">
        {% endif %}
        <div>
            {{ _("This certifies that") }}
        </div>

        <div class="certificate-name" style="">
            {{ member.full_name }}
        </div>
        <div>
            {{ _("has successfully completed the course on") }}
            <b> {{ course.title }} </b>
            on {{ frappe.utils.format_date(certificate.issue_date, "medium") }}.
        </div>

        <table class="certificate-footer">
            <tr>
                 {% if instructors %}
                <td>
                    <div class="certificate-footer-item cursive-font">
                        {% for i in instructors %}
    					{{ frappe.db.get_value("User", i, "full_name") }}
    					{% if not loop.last %}
    					,
    					{% endif %}
    					{% endfor %}
                    </div>
                    <hr class="certificate-divider">
                    <div class="text-center"> {{ _("Course Instructor") }} </div>
                </td>
                {% endif %}

                {% if certificate.expiry_date %}
                <td style="width: 30%"></td>

                <td class="certificate-expiry">
                    <div class="certificate-footer-item">
                        {{ frappe.utils.format_date(certificate.expiry_date, "medium") }}
                    </div>
                    <hr class="certificate-divider">
                    <div class="text-center"> {{ _("Expiry Date") }} </div>
                </td>
                {% endif %}
                </tr>
        </table>
    </div>
    </div>
