script:
frappe.call({
  method: "lms.api.api.get_lms_courses",
  callback: function (response) {
    const courses = response.message;
    const courseContainer = root_element.querySelector(".course-container");

    if (courses && courses.length > 0) {
      courseContainer.innerHTML = ""; 
      courses.forEach(course => {
        const courseCard = `
          <div class="course-card">
            <div class="card-header">
              <img src="${course.image || '/assets/default-course-image.jpg'}" alt="Course Image" class="course-image">
            </div>
            <div class="card-body">
              <h3 class="course-title">${course.title || 'No Title'}</h3>
              <p class="course-duration"><strong>Duration:</strong> ${course.duration__month || 'N/A'} month(s)</p>
              <p class="course-term-start"><strong>Term Start Date:</strong> ${course.term_start_date || 'N/A'}</p>
            </div>
            <div class="card-footer">
              <button class="enroll-button" onclick="window.location.href='https://erp-ryss.ap.gov.in/app/student-registration'">Enroll Now</button>
            </div>
          </div>
        `;
        courseContainer.innerHTML += courseCard;
      });
    } else {
      courseContainer.innerHTML = "<p>No courses available at the moment.</p>";
    }
  },
  error: function (err) {
    const courseContainer = root_element.querySelector(".course-container");
    courseContainer.innerHTML = "<p>Unable to fetch courses. Please try again later.</p>";
    console.error("Error fetching courses:", err);
  }
});

style:
.course-card {
  width: 300px;
  border: 1px solid #ddd;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.course-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 6px 10px rgba(0, 0, 0, 0.15);
}

.card-header {
  width: 100%;
  height: 180px;
  overflow: hidden;
}

.course-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.card-body {
  padding: 16px;
}

.course-title {
  font-size: 1.2em;
  margin-bottom: 8px;
  color: #333;
}

.course-description {
  font-size: 0.9em;
  color: #666;
  margin-bottom: 12px;
}

.course-duration {
  font-size: 0.9em;
  font-weight: bold;
  color: #444;
}

.card-footer {
  padding: 16px;
  text-align: center;
  background-color: #f9f9f9;
}

.enroll-button {
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  background-color: #007bff;
  color: #fff;
  font-size: 1em;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.enroll-button:hover {
  background-color: #0056b3;
}
.course-container {
  display: flex;
  flex-wrap: wrap; /* Wrap cards to the next row */
  gap: 20px; /* Add space between cards */
  justify-content: center; /* Center the cards horizontally */
}

.course-card {
  flex: 1 1 calc(33.333% - 20px); /* Each card takes 1/3 of the row, with gaps */
  max-width: calc(33.333% - 20px);
  box-sizing: border-box;
}
