css:
.certificate-container {
    font-family: "Inter" sans-serif;
    font-size: 20px;
}

.inner-border {
    border: 8px solid #0089FF;
    border-radius: 8px;
    text-align: center;
    padding: 6rem 4rem;
    background-color: #FFFFFF;
}

.certificate-logo {
    height: 3rem;
    margin-right: 0.5rem;
}

.certificate-name {
    font-size: 2rem;
    font-weight: 500;
    color: #192734;
    margin-bottom: 0.5rem;
}

.certificate-footer {
    margin: 4rem auto 0;
    width: 70%;
    text-align: center;
}

.certificate-footer-item {
    color: #192734;
}

.cursive-font {
    font-family: cursive;
    font-weight: 600;
}

.certificate-divider {
    margin: 0.5rem 0;
}

.certificate-expiry {
    margin-left: 2rem;
}

html:
{% set member = frappe.db.get_value("User", doc.member, ["full_name"], as_dict=True) %}
{% set course = frappe.db.get_value("LMS Course", doc.course, ["title", "name", "image"], as_dict=True) %}
{% set logo = frappe.db.get_single_value("Website Settings", "banner_image") %}
{% set brand_name = frappe.db.get_single_value("Website Settings", "app_name") %}
{% set instructors = frappe.get_all("Course Instructor", {"parent": doc.course}, pluck="instructor", order_by="idx") %}

<meta name="pdfkit-orientation" content="Landscape">
<link rel="preconnect" href="https://fonts.googleapis.com">
<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
<link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500&display=swap" rel="stylesheet">

<div class="certificate-container">
   <div style="margin-bottom: 13rem;">
        {% if logo %}
        <img src="{{ logo }}" class="certificate-logo"> <span style="font-weight: 900; font-size: 1.5rem;"> {{ brand_name }} </span>
        {% else %}
        <span style="font-weight: 900; font-size: 1.5rem;"> {{ brand_name }} </span>
        {% endif %}
    </div>

    <div style="margin-bottom: 1rem;">
        {{ _("This is to certify that") }}
    </div>

    <div style="margin-bottom: 1rem; font-weight: 900; font-size: 1.5rem;">
        {{ member.full_name }}
    </div>

    <div style="margin-bottom: 12rem; width: 70%;">
        {{ _("has successfully completed the") }}
        <span style="font-weight: 900;"> {{ course.title }} </span> {{ _("course") }}
        {{ _("including all required assessments") }}
        on {{ frappe.utils.format_date(doc.issue_date, "medium") }}.
    </div>

    <div>
        {% if doc.evaluator %}
        <div>
            <div style="margin-bottom: 0.5rem; color: gray; font-weight: 500;">
                {{ _("Evaluated By")}}
            </div>
            <div style="font-weight: 900;">
                {{ doc.evaluator_name }}
            </div>
        </div>
        {% else %}
        <div style="margin-bottom: 0.5rem; color: gray; font-weight: 500;">
            {{ _("Instructor") if instructors|length == 1 else _("Instructors") }}
        </div>
        <div>
            {% for instructor in instructors %}
                {% set instructor_name = frappe.db.get_value("User", instructor, "full_name") %}
                <span style="font-weight: 900;">
                    {{ instructor_name }}{% if not loop.last %}, {% endif %}
                </span>
            {% endfor %}
        </div>
        {% endif %}
    </div>
</div>
